class LockEntity {
    constructor(options) {
        this.key = options.key;
        this.from = options.from;
    }

    whenReceived(client) {
        // players may receive locks for CRDTs, even though they don't
        // receive creation notifications
        if (client.entities[this.key]) {
            client.entities[this.key].meta.locked = true;
        }
    }

    toString() {
        return `LockEntity{\n\tkey:${this.key}\n}`;
    }
}

module.exports = {
    LockEntity
};