class PNCounter {
    constructor(options) {
        this.key = options.key;
        // count will not stay up-to-date unless
        // the client polls pn-counter/get 
        this.count = options.count;
        this.meta = options.meta || {};
    }

    whenReceived(client) {
        client.entities[this.key] = this;
    }

    toString() { return `PNCounter{\n\tcount:${this.count}\n\tmeta:${JSON.stringify(this.meta)}\n}`; }
}

module.exports = { PNCounter };
